import React from "react";

const FormSignupInput = ({
  id,
  label,
  type,
  name,
  placeholder,
  register,
  errors,
}) => {
  return (
    <div className="mb-3">
      <label htmlFor={props.id} className="float-left ml-5">
        {label} <span className="text-red-600">*</span>
      </label>
      <br />
      <input
        className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
        type={type}
        {...register(id, {
          required: "Obavezno",
          minLength:
            type === "password"
              ? {
                  value: 8,
                  message: "TOO SHORT",
                }
              : null,
          pattern:
            name === "email"
              ? {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: "invalid email address",
                }
              : null,
        })}
        name={name}
        placeholder={placeholder}
        id={id}
      />
      {console.log(errors, "error", id)}
      {errors[props.id] && errors[id].message}
    </div>
  );
};

export default FormSignupInput;
