import React from "react";
import { useForm } from "react-hook-form";

const FormSignupInput = (props) => {
  return (
    <div className="mb-3">
      <label htmlFor={props.id} className="float-left ml-5">
        {props.label} <span className="text-red-600">*</span>
      </label>
      <br />
      <input
        className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
        type={props.type}
        {...props.register(props.id, {
          required: "Obavezno",
          minLength:
            props.type === "password"
              ? {
                  value: 8,
                  message: "TOO SHORT",
                }
              : null,
          pattern:
            props.name === "email"
              ? {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: "invalid email address",
                }
              : null,
        })}
        name={props.name}
        placeholder={props.placeholder}
        id={props.id}
      />
      {console.log(props.errors, "error", props.id)}
      {props.errors[props.id] && props.errors[props.id].message}
    </div>
  );
};

export default FormSignupInput;
