import React, { useState } from "react";
import { useForm } from "react-hook-form";
import "./FormSignup.css";

const FormSignup = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();
  const onSubmit = (values) => {
    setSubmitting(true);

    console.log(values);

    setSubmitting(false);
  };
  const [submitting, setSubmitting] = useState(false);

  return (
    <div className="w-11/12 inline-block">
      <br />
      <form onSubmit={handleSubmit(onSubmit)}>
        <h1>Napravite nalog!!!!!!!</h1>
        <br />

        <div>
          <label htmlFor="email" className="float-left ml-2">
            Email adresa
          </label>
          <br />
          <input
            className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
            type="text"
            {...register("email", {
              required: "Required",
              pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                message: "invalid email address",
              },
            })}
            name="email"
            placeholder="Unesite email"
            id="email"
          />
          <i class="far fa-question-circle ml-2"></i>
          {errors.email && errors.email.message}
          {console.log(errors)}
        </div>
        <br />

        <div>
          <label htmlFor="firstName" className="float-left ml-2">
            Ime
          </label>
          <br />
          <input
            className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
            type="text"
            {...register("firstName", {
              required: "Required",
            })}
            name="firstName"
            placeholder="Unesite ime"
            id="firstName"
          />
          <i class="far fa-question-circle ml-2"></i>
          {errors.firstName && errors.firstName.message}
        </div>
        <br />

        <div>
          <label htmlFor="lastName" className="float-left ml-2">
            Prezime
          </label>
          <br />
          <input
            className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
            type="text"
            {...register("lastName", {
              required: "Required",
            })}
            name="lastName"
            placeholder="Unesite prezime"
            id="lastName"
          />
          <i class="far fa-question-circle ml-2"></i>
          {errors.lastName && errors.lastName.message}
        </div>
        <br />

        <div>
          <label htmlFor="password" className="float-left ml-2">
            Lozinka
          </label>
          <br />
          <input
            className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
            type="password"
            {...register("password", {
              required: "Required",
              minLength: { value: 10, message: "TOO SHORT" },
            })}
            name="password"
            placeholder="Unesite password"
            id="password"
          />
          <i class="far fa-question-circle ml-2"></i>
          {errors.password && errors.password.message}
        </div>
        <br />

        <div>
          <label htmlFor="password2" className="float-left ml-2">
            Potvrdite lozinku
          </label>
          <br />
          <input
            className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
            type="password"
            {...register("password2", {
              required: "Required",
            })}
            name="password2"
            placeholder="Potvrdite password"
            id="password2"
          />
          {errors.password2 && errors.password2.message}
        </div>
        <br />

        <div>
          <label htmlFor="date" className="float-left ml-2">
            Datum rođenja
          </label>
          <br />
          <input
            className="w-11/12 py-2 px-4 border rounded-lg text-sm text-center"
            type="date"
            {...register("date", {
              required: "Required",
            })}
            name="date"
            placeholder="Potvrdite password"
            id="date"
          />
          <i class="far fa-question-circle ml-2"></i>
          {errors.date && errors.date.message}
        </div>
        <br />

        <button
          type="submit"
          className="w-full py-2 px-4 border rounded-lg text-center"
          disabled={submitting}
        >
          Registruj se
        </button>
      </form>
      <br />
      <br />

      <p className="float-left ml-2">Vec imate nalog?</p>
      <button className="w-full py-2 px-4 border rounded-lg text-center">
        Ulogujte se
      </button>
    </div>
  );
};

export default FormSignup;
